mapper.select:

select favoriteid,thumbnail,title,authors,url 
FROM(
    SELECT /*+INDEX_ASC(tbl_favorite tbl_favorite_pk)*/ rownum rn,favoriteid,userid,thumbnail,title,authors,url
	FROM tbl_favorite WHERE userid = #{userid} and rownum <= #{cri.pageNum} * #{cri.amount}
)
where rn > ((#{cri.pageNum}-1)*#{cri.amount})
